<script>
  export default {
    name: 'HarryCharacter',
    props: {
      getRandomInt: Function,
      makeTalk: Boolean
    },
    data() {
      return {
        mouthInterval: null,
        activeMouth: null,
        talkTimer: null,
        talking: false,
        mouthIds: [
          'mouth-1',
          'mouth-2',
          'mouth-closed'
        ]
      }
    },
    watch: {
      mouthInterval: function() {
        if(this.mouthInterval > 0) {
          this.talk();
        }
      },
      talking: function() {
        if(!this.talking) {
          this.activeMouth === 'mouth-1'
        } 
      },
      makeTalk: function() {
        if(this.makeTalk) {
          this.setMouthInterval();
        }
      },
    },
    computed: {
      openMouthIsActive() {
        return this.activeMouth === 'mouth-closed'
      }
    },
    mounted() { 
      this.setMouthInterval();
    },
    methods: {
      setMouthInterval() {
        this.mouthInterval = this.getRandomInt(5000, 10000);
      },
      idMatchesActiveMouth(idString) {
        return this.activeMouth === idString;
      },
      talk() {
        if(!this.talking) {
          this.talking = true;  
          var timeleft = this.getRandomInt(10, 100);
          this.talkTimer = setInterval(() => {
            if(timeleft <= 0){
              clearInterval(this.talkTimer);
              this.talking = false;
            } else {
              this.selectRandomMouth();
            }
            timeleft -= 1;
          }, 100); 
        }     
      },
      selectRandomMouth() {
        let randomMouth = this.mouthIds[Math.floor(Math.random()*this.mouthIds.length)];
        this.activeMouth = randomMouth;
      }
    }
  };
</script>

<style>
  #harry-character {
    width: 130%;
  }

  @keyframes headBob {
    100% {
      transform: scale(0.999);
    }

    to {
      transform: scale(1.01);
    }
  }

  #head.bobbing {
    animation-name: headBob;
    animation-duration: .2s;
    animation-timing-function: ease-in-out;
    animation-delay: 0;
    animation-iteration-count: infinite;
    animation-direction: alternate-reverse;
  }

  #left-eyebrow {
    transition: all .2s;
  }

  #left-eyebrow.wiggling {
    transform: translateY(-2.25%);
  }

  @keyframes shoulderBounce {
    100% {
      transform: translateY(0.5px);
    }

    10% {
      transform: translateY(-0.5px);
    }
  }

  #left-arm.bobbing,
  #right-arm.bobbing {
    animation-name: shoulderBounce;
    animation-duration: .2s;
    animation-timing-function: ease-in-out;
    animation-delay: 0;
